INSERT INTO public.product_description (id, created_at, deleted_at, description, info, platform, policy, tutorial) VALUES (132, '2025-06-17 13:53:35.685571', NULL, '<p><strong><em>Demonschool</em></strong> là một tựa game nhập vai chiến thuật mới lạ, kết hợp giữa yếu tố kinh dị và cuộc sống học đường, được phát triển bởi <strong>Necrosoft Games</strong> và phát hành bởi <strong>Ysbryd Games</strong>. Trò chơi đưa người chơi vào vai <strong>Faye</strong>, một sinh viên tại trường đại học trên một hòn đảo bí ẩn, nơi ranh giới giữa thế giới người và quái vật mờ nhạt. Bạn sẽ cùng nhóm bạn đồng hành đối mặt với những sinh vật kỳ dị, đồng thời quản lý lịch học và xây dựng mối quan hệ trong môi trường học đường đầy thử thách.</p><p><strong>Tính năng nổi bật:</strong></p><ul class="list-disc ml-3"><li><p><strong>Chiến thuật theo lượt:</strong> Lập kế hoạch hành động trong giai đoạn chuẩn bị và thực hiện đồng loạt trong giai đoạn hành động. Bạn có thể quay lại các bước trước đó để tối ưu chiến thuật.</p></li><li><p><strong>Cốt truyện kinh dị nhẹ:</strong> Khám phá câu chuyện qua các nhân vật với tạo hình lớn và những nhiệm vụ phụ phong phú.</p></li><li><p><strong>Quản lý thời gian:</strong> Lên lịch học để nâng cao kỹ năng và phát triển nhân vật.</p></li><li><p><strong>Xây dựng mối quan hệ:</strong> Tương tác với 15 nhân vật để tạo dựng tình bạn và ảnh hưởng đến diễn biến cốt truyện.</p></li></ul>', '<ul class="list-disc ml-3"><li><p><strong>Ngày phát hành:</strong> Quý 3 năm 2025</p></li><li><p><strong>Nền tảng hỗ trợ:</strong> Windows, macOS, Linux, PlayStation 4, PlayStation 5, Xbox One, Xbox Series X|S, Nintendo Switch</p></li><li><p><strong>Ngôn ngữ hỗ trợ:</strong> Tiếng Anh, Nhật, Trung giản thể, Pháp, Tây Ban Nha (Mỹ Latinh)</p></li><li><p><strong>Độ tuổi:</strong> Dành cho người chơi từ 17 tuổi trở lên</p></li></ul>', '<p><strong>Yêu cầu tối thiểu (PC):</strong></p><ul class="list-disc ml-3"><li><p><strong>Hệ điều hành:</strong> Windows 10 (64-bit)</p></li><li><p><s