INSERT INTO public.product_description (id, created_at, deleted_at, description, info, platform, policy, tutorial) VALUES (134, '2025-06-17 13:57:57.860342', NULL, '<p><strong><em>High on Life 2</em></strong> là phần tiếp theo được mong đợi của tựa game hành động bắn súng góc nhìn thứ nhất kết hợp yếu tố hài hước, được phát triển bởi <strong>Squanch Games</strong>. Trò chơi tiếp tục hành trình của người chơi trong vũ trụ đầy màu sắc và kỳ quái, với một cốt truyện mới, vũ khí nói chuyện độc đáo và môi trường đa dạng.</p><p><strong>Tính năng nổi bật:</strong></p><ul class="list-disc ml-3"><li><p><strong>Cốt truyện mới:</strong> Khám phá một âm mưu vũ trụ mới đe dọa sự tồn vong của nhân loại.</p></li><li><p><strong>Vũ khí nói chuyện:</strong> Tiếp tục sử dụng các vũ khí đặc biệt với tính cách riêng biệt, bao gồm cả những khẩu súng mới như shotgun và cung tên.</p></li><li><p><strong>Môi trường đa dạng:</strong> Di chuyển qua các địa điểm kỳ lạ và phong phú, từ thành phố tương lai đến các khu vực hoang dã.</p></li><li><p><strong>Gameplay cải tiến:</strong> Trải nghiệm cơ chế chơi mới mẻ, bao gồm cả việc sử dụng ván trượt để di chuyển nhanh chóng trong môi trường.</p></li></ul>', '<ul class="list-disc ml-3"><li><p><strong>Ngày phát hành:</strong> Mùa đông năm 2025</p></li><li><p><strong>Nền tảng hỗ trợ:</strong> Windows PC, Xbox Series X|S, Xbox Cloud Gaming, Xbox Play Anywhere</p></li><li><p><strong>Hỗ trợ Game Pass:</strong> Có mặt ngay ngày đầu phát hành cho người dùng Xbox Game Pass Ultimate và PC Game Pass</p></li><li><p><strong>Độ tuổi:</strong> Dành cho người chơi từ 17 tuổi trở lên</p></li></ul>', '<p><strong>Yêu cầu tối thiểu (PC):</strong></p><ul class="list-disc ml-3"><li><p><strong>Hệ điều hành:</strong> Windows 10 (64-bit)</p></li><li><p><strong>Bộ xử lý:</strong> Quad-core 2.5 GHz</p></li><li><p><strong>RAM:</strong> 8 GB</p></li><li><p><strong>Đồ họa:</strong> NVIDIA GTX 1060 / AMD RX 580</p></li><li><p><strong>Dung lượng ổ cứng:</strong> 50 GB trống</p></l